export default function decideWinner(previousStonePosition, board) {

    //Check horizontal line.
    const start = Math.min(0, previousStonePosition.col - 4);
    const end = Math.min(18, previousStonePosition.col + 4);

    let count = 0;
    for(let i = start; i <= end; i++) {
        if(board[previousStonePosition.row][previousStonePosition.col] === board[previousStonePosition.row][i]) {
            count++;
        } else {
            count = 0;
        }

        if(count === 5) {
            return board[previousStonePosition.row][previousStonePosition.col];
        }
    }

    return null;
}
