import { useState, useEffect } from 'react';
import topLeftCorner from './images/topLeftCorner.png';
import topRightCorner from './images/topRightCorner.png';
import bottomLeftCorner from './images/bottomLeftCorner.png';
import bottomRightCorner from './images/bottomRightCorner.png';
import upLine from './images/upLine.png';
import downLine from './images/downLine.png';
import leftLine from './images/leftLine.png';
import rightLine from './images/rightLine.png';
import crossLine from './images/crossLine.png';
import whiteStone from './images/whiteStone.png';
import blackStone from './images/blackStone.png';
import './App.css';
import decideWinner from './decideWinner';

let connection = null;

export default function Board() {

    const [board, setBoard] = useState(Array(19).fill(Array(19).fill(null)));
    const [turn, setTurn] = useState('black');
    const [color, setColor] = useState(null);
    const [winner, setWinner] = useState(null);

    console.log('color: ' + color + ' turn: ' + turn);

    const rows = [];
    for (let i = 0; i < 19; i++) {
        rows.push(<Row rowNumber={i}
            board={board}
            setBoard={setBoard}
            turn={turn}
            setTurn={setTurn}
            color={color}
            winner={winner}
            setWinner={setWinner}
        />);
    }

    function onMessage(message) {
        console.log('1' + color);
        const data = JSON.parse(message.data);
        switch (data['type']) {
            case 'SET_STONE_COLOR':
                setColor(data.color);
                console.log('Setted to ' + data.color + '!');
                break;
            case 'GET_OPPONENT_POSITION':
                const nextBoard = board.map(row => {
                    return row.slice();
                });
    
                const row = data.row;
                const col = data.col;
                nextBoard[row][col] = data.color;
                
                setBoard(nextBoard);
                if(data.color === 'white') {
                    setTurn('black');
                } else {
                    setTurn('white');
                }

                break;
            default:
                break;
        } 
    }

    useEffect(() => {
        connection = new WebSocket("ws://localhost:8080/omok");
        console.log('Connected to server!');

        return () => {
            connection.close();
            console.log('Disconnected from server!');
        };
    }, []);
    
    useEffect(() => {
        if(connection) {
            connection.onmessage = onMessage;
        }
    }, [connection, onMessage])

    let content;
    if(winner) {
        content = 'Winner: ' + (winner === 'black' ? 'Black' : 'White');
    } else {
        if(turn === color) {
            content = 'Your turn!';
        } else {
            content = 'Waiting for opponent...';
        }
    }

    return (
        <>
            <p>
                {content}
            </p>
            <div>
                {rows}
            </div>
        </>
    );
}

function Row(props) {

    let leftmost;
    let middle;
    let rightmost;

    if (props.rowNumber === 0) {
        leftmost = topLeftCorner;
        middle = upLine;
        rightmost = topRightCorner;
    } else if (props.rowNumber === 18) {
        leftmost = bottomLeftCorner;
        middle = downLine;
        rightmost = bottomRightCorner;
    } else {
        leftmost = leftLine;
        middle = crossLine;
        rightmost = rightLine;
    }

    const row = [];
    row.push(<Square line={leftmost} position={0} {...props} />);
    for (let i = 1; i <= 17; i++)
        row.push(<Square line={middle} position={i} {...props} />);
    row.push(<Square line={rightmost} position={18} {...props} />);

    return (
        <div>
            {row}
        </div>
    );
}

function Square({ line, rowNumber, position, board, setBoard, turn, setTurn, color, winner, setWinner }) {

    function handleClick() {
        if (board[rowNumber][position])
            return;

        const nextBoard = board.map(row => {
            return row.slice()
        });

        nextBoard[rowNumber][position] = color;
        
        const StonePositionMessage = {
            type: 'GET_OPPONENT_POSITION',
            color: color,
            row: rowNumber,
            col: position
        };

        connection.send(JSON.stringify(StonePositionMessage));
        setWinner(decideWinner({row: rowNumber, col: position}, nextBoard));
        setTurn(turn === 'white' ? 'black' : 'white');
        setBoard(nextBoard);
    }

    let stone = null;
    if (board[rowNumber][position] === 'white') {
        stone = <img src={whiteStone} width='25px' height='25px' alt='whiteStone' style={{ position: 'relative', top: '-28px', left: '1px', zIndex: 1 }} />;
    } else if (board[rowNumber][position] === 'black') {
        stone = <img src={blackStone} width='25px' height='25px' alt='blackStone' style={{ position: 'relative', top: '-28px', left: '1px', zIndex: 1 }} />;
    } else {
        stone = null;
    }

    return (
        <div className='square' onClick={() => {
            if(!winner && turn === color)
                handleClick();
        }}>
            <img src={line} width='25px' height='25px' alt='pattern' style={{ position: 'relative', top: '0px', left: '0px', zIndex: 0 }} />
            {stone}
        </div>
    );
}
