let connection = null;

export function useConnection(url) {
    useEffect(() => {
        connection = new WebSocket(url);
        console.log('Connected to server!');

        return () => {
            connection.close();
            console.log('Disconnected from server!');
        };
    }, []);
}

export function useMessage(onMessage) {
    useEffect(() => {
        if(connection) {
            connection.onmessage = onMessage;
        }
    }, [onMessage]);    
}
